import React, { Component } from "react";
import { connect } from "react-redux";
import authOperations from "../redux/auth/authOperations";

class RegisterView extends Component {
  state = {
    // name: "",
    email: "",
    password: "",
  };
  handleChange = ({ target: { name, value } }) => {
    this.setState({ [name]: value });
  };
  handleSubmit = (evt) => {
    //const { name, email, password } = this.state;
    evt.preventDefault();
    this.props.onRegister({ ...this.state });
    this.setState({ email: "", password: "" });
  };
  render() {
    const { email, password } = this.state;

    return (
      <section className="wrapper">
        {/* <CSSTransition
        in={alertMessageShow}
        timeout={250}
        classNames="alertFade"
        unmountOnExit
      > */}
        {/* <button className="alertMessage" onClick={this.toggleAlert}>
          <p>Contact already exists</p>
        </button> */}
        {/* </CSSTransition> */}
        <h1>Register form</h1>
        <form onSubmit={this.handleSubmit} className="form">
          {/* <label>
            Name
            <input
              className="input"
              type="text"
              name="name"
              value={name}
              onChange={this.handleChange}
              placeholder="Enter name"
            ></input>
          </label> */}
          <label>
            Email
            <input
              className="input"
              type="email"
              value={email}
              onChange={this.handleChange}
              name="email"
              placeholder="Enter email"
            ></input>
          </label>
          <label>
            Password
            <input
              className="input"
              type="password"
              value={password}
              onChange={this.handleChange}
              name="password"
              placeholder="Enter password"
            ></input>
          </label>
          <button type="submit" className="addContact-button">
            Sign Up
          </button>
        </form>
      </section>
    );
  }
}

export default connect(null, { onRegister: authOperations.register })(
  RegisterView
);
